function Logger(){
  this.logs = [ ];
}

Logger.prototype.log = function(userlogs){
  var self = this;
  if(arguments.length){
    Array.prototype.slice.call(arguments).forEach(function (data){
      if(typeof data === 'object'){
        try {
          self.logs.push(JSON.stringify(data))
        }catch(e){
          self.logs.push(String(data));
        }
      } else {
        self.logs.push(String(data));
      }
    })
  }  
}

Logger.prototype.getLogs = function(){
  return this.logs;
}

module.exports = Logger;
